import axios from "axios";
import appConfig from "../Utils/Config";

class DataService {
  async sendAndReceiveInitialPrompt<T, U>(
    endpoint: string,
    data: T
  ): Promise<U> {
    try {
      console.log(endpoint);
      console.log(data);

      const fastApiBaseUrl = "http://localhost:3002"; // FastAPI URL
      const input = "plan a wedding"; // Your input data

      const url = `${fastApiBaseUrl}/marvin/translate_prompt?input=${encodeURIComponent(
        data
      )}`;
      const response = await axios.post(url);
      //   const jsonData = { input: data };
      //   const response = await fetch(`${appConfig.serverUrl}/${endpoint}`, {
      //     method: "POST",
      //     headers: {
      //       "Content-Type": "application/json",
      //     },
      //     body: JSON.stringify(jsonData),
      //   });
      console.log(JSON.stringify(jsonData));

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      return await response.json();
    } catch (error) {
      console.error("Fetch error:", error);
      throw error;
    }
  }

  async askForMissingValues<T, U>(endpoint: string, data: T): Promise<U> {
    try {
      const response = await fetch(`${appConfig.serverUrl}/${endpoint}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      return await response.json();
    } catch (error) {
      console.error("Fetch error:", error);
      throw error;
    }
  }
  async sendAndReceiveMissingValues<T, U>(
    endpoint: string,
    data: T
  ): Promise<U> {
    try {
      const response = await fetch(`${appConfig.serverUrl}/${endpoint}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      return await response.json();
    } catch (error) {
      console.error("Fetch error:", error);
      throw error;
    }
  }
}

const dataService = new DataService();

export default dataService;
