import { useEffect, useState } from "react";
import EventModel from "../../../Models/EventModel";
import dataService from "../../../Services/DataService";
import notifyService from "../../../Services/NotifyService";
import MarvinOutput from "../MarvinOutput/MarvinOutput";
import "./SearchBar.css";

function SearchBar(): JSX.Element {
  const [neededUrl, setNeededUrl] = useState<string>("");
  const [currentEvent, setCurrentEvent] = useState<EventModel>();
  const [outputText, setOutputText] = useState("");
  const inputElement = document.getElementById("InputText") as HTMLInputElement;
  //   const outputElement = document.getElementById("AIOutput") as HTMLDivElement;

  const hasNullProperty = (obj: EventModel): boolean => {
    for (const value of Object.values(obj)) {
      if (value === null) {
        return true;
      }
    }
    return false;
  };


  async function sendToBackend() {
    //extract input text
    //send to backend

    //if there is no event yet
    try {
      if (!currentEvent) {
        setNeededUrl("translate_prompt");

        const updatedEvent = new EventModel(
          await dataService.sendAndReceiveInitialPrompt(
            neededUrl,
            inputElement.value.toString()
          )
        );
        setCurrentEvent(updatedEvent);

        //if the event is missing values of the event model

        if (hasNullProperty(currentEvent)) {
          //update ai chat text...
          setNeededUrl("ask_For_Blanks");
          //   console.log(
          //     setOutputText(
          //       await dataService.askForMissingValues(neededUrl, currentEvent)
          //     )
          //   );

          setOutputText(
            await dataService.askForMissingValues(neededUrl, currentEvent)
          );
        }
      }
      if (hasNullProperty(currentEvent)) {
        console.log("missing values...");

        setNeededUrl("fill_In_Blanks");
        setCurrentEvent(
          await dataService.sendAndReceiveMissingValues(
            neededUrl,
            inputElement.value
          )
        );
      }

      if (currentEvent && !hasNullProperty(currentEvent)) {
        // recommend places that fit the event values...
        console.log("recommending...");
      }
    } catch (err: any) {
      notifyService.error(err);
    }
  }

  return (
    <div className="SearchBar">
      {/* search bar and enter: */}
      <input id="InputText" className="InputBox" type="text"></input>
      <br />
      <button className="SearchButton" onClick={() => sendToBackend()}>
        Help Me
      </button>
      <MarvinOutput text={outputText} />
    </div>
  );
}

export default SearchBar;
